import { useContext, useState } from 'react';
import { AuthContext } from '../context';
import MyButton from './MyButton/MyButton';

const Product = ({name,description,img,price}) => {
    const{amount,setAmount} = useContext(AuthContext);
    const {product,setProduct}= useContext(AuthContext);

    const addToCart = () =>{
        setAmount(amount + 1);
        
        console.log(product);
    }
    return (
        <div className='product-card'>
            <img className='product-bmx-image' src={img} alt="" />
            <h1>{name}</h1>
            <p>{description}</p>
            <span>{price}</span>
            <MyButton addToCart={addToCart}>В корзину</MyButton>
        </div>
    )
}

export default Product
