import React, { useContext, useState } from 'react'
import CartProducts from './CartProducts';
import cart from '../img/cart.png'
import { AuthContext } from '../context';

const Cart = () => {
    // Цена
    const bikes = useSelector(state => state.cart.itemsInCart);
    const totalPrice = bikes.reduce((acc,bike) => acc += bike.price,0);

    const [isOpenCart, setOpenCart] = useState(false);
    const openAndCloseCart = () => {
        setOpenCart(!isOpenCart);
      
    }
    return (
        <div className='cart-block'>
            <div className='cart' onClick={openAndCloseCart}>
                <div className='amountProductOfCart'><span>1</span></div>
                <li  className={`navigation-item ${isOpenCart && 'activeCart'}`}><img className='cart-ico' src={cart} alt="" /></li>
            </div>
            <div className='openCart'>
                {isOpenCart && (<div className='shopCart'><span onClick={() => setOpenCart(false)} className='shopCart-close'>X</span><CartProducts totalPrice={totalPrice}/></div>)}
            </div>
        </div>
        
    )
}

export default Cart
