import { createBrowserHistory } from 'history';
import { useDispatch, useSelector } from 'react-redux';
import { Link } from 'react-router-dom';
import { setCurrentBike } from '../redux/bikes/reducer';
import { deleteItemFromCart, setItemInCart } from '../redux/cart/reducer';
import MyButton from './MyButton/MyButton';

const Product = ({bike}) => {

    const dispatch = useDispatch();

    // Открытие товара
    const history = createBrowserHistory();
    const openProductViewBike = (event) =>{
    event.stopPropagation();
    dispatch(setCurrentBike(bike))
    console.log(bike.title)
    // Пушим в адресную строку
    // history.push(`/bikes/${bike.id}`) 
    console.log(bike)
  }
 
    //Добавление товара в корзину и удаление из корзины 
    const items = useSelector(state => state.cart.itemsInCart);
    const isItemInCart = items.some(item => item.id === bike.id )

    const addToCartAndDelete = (event) =>{
        event.stopPropagation();
        if(isItemInCart){
            dispatch(deleteItemFromCart(bike.id))
        }else{
            dispatch(setItemInCart(bike));
        }
        
    }
    return (
        <div onClick={openProductViewBike} className='product-card'>
            <Link to={"/bikes/" + bike.id}></Link>
            <img className='product-bmx-image' src={bike.img} alt="" />
            <h1>{bike.name}</h1>
            <p>{bike.description}</p>
            <span>{bike.price}</span>
            <MyButton type={isItemInCart ? 'myButton activeInCartButton' : 'myButton '}  addToCartAndDelete={addToCartAndDelete}>{isItemInCart ? 'Удалить из корзины' : 'В корзину'}</MyButton>
        </div>
    )
}

export default Product
