import React, { useState } from 'react'
import { Link } from 'react-router-dom'
import cabinet from '../img/cabinet.png'
import cart from '../img/cart.png'

const NavBar = () => {
    const [isOpenCart, setOpenCart] = useState(false);
    const openAndCloseCart = () =>{
        setOpenCart(!isOpenCart);
        console.log(isOpenCart);
    }
    return (
        <div className='navbar'>
            <nav className='navigation'>
            <ul className='navigation-list'>
                <Link className='link header-link' to='/'><li className='navigation-item'>Main</li></Link>
                <Link className='link header-link' to='/bikes'><li className='navigation-item'>Bikes</li></Link>
                <Link className='link header-link' to='/about'><li className='navigation-item'>About</li></Link>
                <Link className='link header-link' to='/contacts'><li className='navigation-item'>Contacts</li></Link>
                <Link  className='link header-link' to='/cart'><li onClick={openAndCloseCart} className={`navigation-item ${isOpenCart && 'activeCart'}`}><img className='cart-ico' src={cart} alt="" /></li></Link>
                {isOpenCart && (<div className='shopCart'><span onClick={() => setOpenCart(false)} className='shopCart-close'>X</span>Товар</div>)}
                <Link className='link header-link' to='/cabinet'><li className='navigation-item'><img className='cabinet-ico' src={cabinet} alt="" /></li></Link>
            </ul>
        </nav>
        </div>
        
    )
}

export default NavBar
