import React, { useState } from 'react'
import cabinet from '../img/cabinet.png'

const Cabinet = () => {
  const [isOpenCabinet,setIsOpenCabinet] = useState(true);
  const openAndCloseCabinet = () => {
    setIsOpenCabinet(!isOpenCabinet);
}
  return (
    <div>
      <div onClick={openAndCloseCabinet} className='navigation-item'><img className='cabinet-ico' src={cabinet} alt="" /></div>
      <div className='openCart'>
        {isOpenCabinet && (<div className='cabinetPopUp'>hello</div>)}
      </div>
    </div>

  )
}
{$(document).mouseup(function (e) {
    var container = $(".cabinetPopUp");
    if (container.has(e.target).length === 0){
        container.hide();
    }
})}

export default Cabinet;
