import React, { useContext, useState } from 'react'

import Product from '../components/Product'

const Main = ({bikes}) => {
  const dispatch = useDispatch();

    // Открытие товара
    const history = createBrowserHistory();
    const openProductViewBike = (bike) =>{
    dispatch(setCurrentBike(bike))
    history.push(`/main/${bike.title}`)
  }
  return (
    <main className='main'>
        <div className='banner'>
          <h1 className='banner-text'>Enjoy and buy right now</h1>
        </div>
        {bikes.map(bike => <Product openProductViewBike={openProductViewBike} key={bike.id} bike={bike}/>)}
    </main>   
  )
}
export default Main
