import React, { useContext } from 'react'
import { useSelector } from 'react-redux';
const CartProducts = ({ bikeItems }) => {
  // Цена
  const totalPrice = bikeItems.reduce((acc, bike) => acc += bike.price, 0);
  const goOrder = () => {
    console.log('you made an order')
    console.log(bikeItems.length)
  }
  return (
    <div>
      {bikeItems.map(bike => {
        <div className='productInCart-block'>
          {/* <img src={bike.img} alt="" width={50} height={50} /> */}
           <p>asdsad</p>
        </div>

      })}
      {bikeItems.length > 0 ?
        <div>
          <h2>Total Price: {totalPrice}</h2>
          <div><button className='orderButton' onClick={goOrder}>Оформить заказ</button></div>
        </div>
        : <h2>Коризна пуста!</h2>}

    </div>
  )
}

export default CartProducts;
