import React, { useContext, useState } from 'react'
import Product from './Product'

const Main = ({bikes}) => {
  
  return (
    <main className='main'>
        <div className='banner'>
          <h1 className='banner-text'>Enjoy and buy right now</h1>
        </div>
        <div className='bikes__section'>
          {bikes.map(bike => <Product key={bike.id} name={bike.name}  description={bike.description} img={bike.img} price={bike.price} />)}
        </div>
        
    </main>
    
  )
}

export default Main
