import { useDispatch, useSelector } from 'react-redux';
import { setItemInCart } from '../redux/cart/reducer';
import MyButton from './MyButton/MyButton';

const Product = ({bike}) => {
    //Удаление товара
    const items = useSelector(state => state.cart.itemsInCart);
    const isItemInCart = items.some(item => item.id === bike.id )




    const dispatch = useDispatch();
    const addToCart = (event) =>{
        event.stopPropagation();
        dispatch(setItemInCart(bike));
    }
    return (
        <div className='product-card'>
            <img className='product-bmx-image' src={bike.img} alt="" />
            <h1>{bike.name}</h1>
            <p>{bike.description}</p>
            <span>{bike.price}</span>
            <MyButton addToCart={addToCart}>{isItemInCart ? 'Удалить из корзины' : 'В корзину'}</MyButton>
        </div>
    )
}

export default Product
