import React, { useContext, useState } from 'react'
import Product from '../components/Product'
import Select from '../components/select/Select'
import { bikes } from "../bikes";
import MySearch from '../components/Search/MySearch';

const Main = () => {
  
  const [bikesArray, setbikesArray] = useState(bikes);
  const [selectedSort, setSelectedSort] = useState('');

  const [searchQuery,setSearchQuery] = useState('');

  const sortBikes = (sort) =>{
    setSelectedSort(sort)
    if(sort === 'name'){
      setbikesArray([...bikesArray].sort((a,b) => a[sort].localeCompare(b[sort]))); // Сортировка в алфавитном порядке
    }
    if(sort === 'price'){
      setbikesArray([...bikesArray].sort((a,b) => a[sort] - b[sort] )); // Сортировка цен по убыванию
    }
    else if(sort === 'price'){
      setbikesArray([...bikesArray].sort((a,b) => b[sort] - a[sort] )); // Сортировка цен по возрастанию
    }
   
  }

  return (
    <main className='main'>
        <div className='banner'>
          <h1 className='banner-text'>Enjoy and buy right now</h1>
        </div>
        <MySearch value={searchQuery} onChange={(event) => setSearchQuery(event.target.value)}/>
        <hr style={{margin : "15px 0px 15px"}}/>
        <button onClick={() => setbikesArray([...bikesArray].sort((a,b) => a['name'].localeCompare(b['name'])))}>По названию</button>
        <button onClick={() => setbikesArray([...bikesArray].sort((a,b) => a['price'] - b['price'] ))}>По убыванию</button>
        <button onClick={() => setbikesArray([...bikesArray].sort((a,b) => b['price'] - a['price'] ))}>По возрастани</button>
        {/* <Select value={selectedSort} onChange={sortBikes} defaultValue="Сортировка" option={[
          {value: 'name', name: "По названию"},
          {value: 'price', name: "Цена по возрастанию"},
          {value: 'price', name: "Цена по убыванию"},
   
        ]}/> */}
        <div className='filter-block'>
            <select value={selectedSort} onChange={event => onChange(event.target.value)} className='filter-select'>
                <option disabled value="">Сортировка</option>
                <option value="name">По названию</option>
                <option value="price">Цена по возрастанию</option>
                <option value="price">Цена по убыванию</option>
                
            </select>
        </div>





        <div className='bikes__section'>
          {bikesArray.map(bike => <Product key={bike.id} bike={bike}/>)} 
        </div>
            
    </main>   
  )
}
export default Main
