import React, { useContext, useState } from 'react'
import Product from '../components/Product'
import Select from '../components/select/Select'
const Main = ({bikes}) => {

  return (
    <main className='main'>
        <div className='banner'>
          <h1 className='banner-text'>Enjoy and buy right now</h1>
        </div>
        <hr />
        <Select defaultValue="Сортировка" option={[
          {value: title}
        ]}/>

        "По названию","Цена по возрастанию","Цена по убыванию"
        <div className='bikes__section'>
          {bikes.map(bike => <Product key={bike.id} bike={bike}/>)} 
        </div>
            
    </main>   
  )
}
export default Main
