import Form from './Form'
const LoginPage = () => {
  const dispatch = useDispatch();
  const handleLogin = (email,password) =>{
    const auth = getAuth();
    signInWithEmailAndPassword(auth,email,password);
  }
  

  return (
    <Form />
  )
}

export default LoginPage
