import React, { useContext } from 'react'
import { useSelector } from 'react-redux';
import MyButton from './MyButton/MyButton';

const CartProducts = () => {
  // Цена
  const bikes = useSelector(state => state.cart.itemsInCart);
  const totalPrice = bikes.reduce((acc,bike) => acc += bike.price,0);
  const goOrder = () =>{
    console.log('you made an order')
  }
  return (
    <div>{bikes.length > 0 
      ? <div className='totalPrice'>

        Total Price: {totalPrice}
        <div><button className='orderButton' onClick={goOrder}>Оформить заказ</button></div>
          
        </div> 
    : <h2 className='emptyCart'>Empty :\</h2>}</div>
  )
}

export default CartProducts
