import { createBrowserHistory } from 'history';
import React, { useContext, useState } from 'react'
import { useDispatch } from 'react-redux';
import Product from '../components/Product'
import { setCurrentBike } from '../redux/bikes/reducer';

const Main = ({bikes}) => {
  const history = createBrowserHistory();
  const dispatch = useDispatch();
  const openProductViewBike = (bike) =>{
    dispatch(setCurrentBike(bike))
    history.push(`/main/${bike.name}`)
  }
  return (
    <main className='main'>
        <div className='banner'>
          <h1 className='banner-text'>Enjoy and buy right now</h1>
        </div>
        {bikes.map(bike => <Product onClick={openProductViewBike} key={bike.id} bike={bike}/>)}
    </main>   
  )
}
export default Main
