import React, { useState } from 'react'
import CartProducts from './CartProducts';
import cart from '../img/cart.png'
const Cart = () => {
    const [isOpenCart, setOpenCart] = useState(false);
    const openAndCloseCart = () => {
        setOpenCart(!isOpenCart);
        console.log(isOpenCart);
    }
    return (
        <div className='cart'>
            <div className='amountProductOfCart'><span>3</span></div>
            <li onClick={openAndCloseCart} className={`navigation-item ${isOpenCart && 'activeCart'}`}><img className='cart-ico' src={cart} alt="" /></li>
            {isOpenCart && (<div className='shopCart'><span onClick={() => setOpenCart(false)} className='shopCart-close'>X</span><CartProducts /></div>)}
        </div>
        
    )
}

export default Cart
