import React, { useContext } from 'react'
import { useDispatch } from 'react-redux';
import { Link, Navigate } from 'react-router-dom'
import { AuthContext } from '../context';
import { useAuth } from '../hooks/use-auth';
import { removeUser } from '../redux/auth/userSlice';

const CabinetPopUp = () => {
  const {isOpenCabinet,setIsOpenCabinet}= useContext(AuthContext);
  const dispatch = useDispatch();
  const { isAuth, email } = useAuth();
  return (
    <div>
      {isAuth ? 
      <div>
        <div onClick={() => setIsOpenCabinet(false)}><Link to='/cabinet'>MyAccount</Link></div> 
        <div onClick={() => {setIsOpenCabinet(false), dispatch(removeUser())}}><Link to='/cabinet'>Exit</Link></div> 
      </div>
      : 
      <div>
        <div onClick={() => setIsOpenCabinet(false)}><Link to='/login'>Login</Link></div>
        <div onClick={() => setIsOpenCabinet(false)}><Link to='/registration'>Create account</Link></div>
      </div>
      }
    </div>
  )
}

export default CabinetPopUp
